import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Paper,
  useTheme,
  Link,
  CircularProgress,
  IconButton,
  Tooltip,
  Snackbar
} from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import TwitterIcon from '@mui/icons-material/Twitter';
import { getPairXToken } from '../../utils/helpers';
import axios from 'axios';

const TimeframeStats = ({ label, txns, volume, priceChange }) => {
  // Add null checks with default values
  const safeVolume = volume ?? 0;
  const safePriceChange = priceChange ?? 0;
  const safeTxns = txns ?? { buys: 0, sells: 0 };

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle2" color="primary.main" gutterBottom>
        {label}
      </Typography>
      <Grid container spacing={2}>
        {/* Transactions */}
        <Grid item xs={12}>
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            mb: 1,
            bgcolor: 'action.selected',
            p: 1,
            borderRadius: 1
          }}>
            <Box>
              <Typography color="text.secondary" variant="body2">Buys</Typography>
              <Typography 
                variant="body1" 
                color="success.main"
                sx={{ fontWeight: 500 }}
              >
                {safeTxns.buys}
              </Typography>
            </Box>
            <Box sx={{ textAlign: 'right' }}>
              <Typography color="text.secondary" variant="body2">Sells</Typography>
              <Typography 
                variant="body1" 
                color="error.main"
                sx={{ fontWeight: 500 }}
              >
                {safeTxns.sells}
              </Typography>
            </Box>
          </Box>
        </Grid>

        {/* Volume and Price Change */}
        <Grid item xs={12}>
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between',
            bgcolor: 'action.selected',
            p: 1,
            borderRadius: 1
          }}>
            <Box>
              <Typography color="text.secondary" variant="body2">Volume</Typography>
              <Typography variant="body1" sx={{ fontWeight: 500 }}>
                ${Number(safeVolume).toLocaleString()}
              </Typography>
            </Box>
            <Box sx={{ textAlign: 'right' }}>
              <Typography color="text.secondary" variant="body2">Price Change</Typography>
              <Typography 
                variant="body1" 
                color={safePriceChange >= 0 ? 'success.main' : 'error.main'}
                sx={{ fontWeight: 500 }}
              >
                {safePriceChange >= 0 ? '+' : ''}{safePriceChange.toFixed(2)}%
              </Typography>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
};

const MarketStats = ({ pairAddress }) => {
  const [marketData, setMarketData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMarketData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`https://api.dexscreener.com/latest/dex/pairs/solana/${pairAddress}`);
        const pairData = response.data.pairs?.[0];
        if (!pairData) {
          throw new Error('No pair data found');
        }
        setMarketData(pairData);
      } catch (err) {
        console.error('Error fetching market data:', err);
        setError('Failed to fetch market data');
      } finally {
        setLoading(false);
      }
    };

    if (pairAddress) {
      fetchMarketData();
    }
  }, [pairAddress]);

  if (loading) return <CircularProgress size={24} />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!marketData) return null;

  const timeframes = [
    { label: 'Last 5 Minutes', key: 'm5' },
    { label: 'Last Hour', key: 'h1' },
    { label: 'Last 6 Hours', key: 'h6' },
    { label: 'Last 24 Hours', key: 'h24' }
  ];

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Market Activity
      </Typography>

      <Grid container spacing={2}>
        {timeframes.map(({ label, key }) => (
          <Grid item xs={12} md={6} key={key}>
            <TimeframeStats 
              label={label}
              txns={marketData.txns?.[key]}
