const TokenHolders = ({ tokenAddress }) => {
  const [holders, setHolders] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHolders = async () => {
      try {
        setLoading(true);
        const response = await axios.post(
          'https://mainnet.helius-rpc.com/?api-key=498ead61-0162-4f2b-b6ec-617ae17935d0',
          {
            jsonrpc: '2.0',
            id: 1,
            method: 'getTokenLargestAccounts',
            params: [tokenAddress]
          }
        );

        if (response.data.result) {
          setHolders(response.data.result.value);
        }
      } catch (err) {
        console.error('Error fetching token holders:', err);
        setError('Failed to fetch token holders');
      } finally {
        setLoading(false);
      }
    };

    if (tokenAddress) {
      fetchHolders();
    }
  }, [tokenAddress]);

  const calculatePercentage = (amount) => {
    if (!holders) return 0;
    const totalSupply = holders.reduce((sum, holder) => sum + Number(holder.amount), 0);
    return (Number(amount) / totalSupply) * 100;
  };

  const calculateTop10Percentage = () => {
    if (!holders) return 0;
    const top10Total = holders.slice(0, 10).reduce((sum, holder) => sum + Number(holder.amount), 0);
    const totalSupply = holders.reduce((sum, holder) => sum + Number(holder.amount), 0);
    return ((top10Total / totalSupply) * 100).toFixed(2);
  };

  const formatAmount = (amount) => {
    return Number(amount).toLocaleString();
  };

  if (loading) return <CircularProgress size={24} />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!holders) return null;

  return (
    <Grid item xs={12}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 2 
      }}>
        <Typography variant="subtitle2" color="primary.main">
          Top Holders
        </Typography>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: 1,
          bgcolor: 'action.selected',
          px: 1.5,
          py: 0.5,
          borderRadius: 1
        }}>
          <Typography variant="body2" color="text.secondary">
            Combined:
          </Typography>
          <Typography 
            variant="body2" 
            sx={{ fontWeight: 500 }}
          >
            {calculateTop10Percentage()}%
          </Typography>
        </Box>
      </Box>

      <Box sx={{ 
        maxHeight: '200px', 
        overflowY: 'auto',
        '&::-webkit-scrollbar': {
          width: '8px',
        },
        '&::-webkit-scrollbar-track': {
          background: 'transparent',
        },
        '&::-webkit-scrollbar-thumb': {
          background: 'rgba(0,0,0,0.1)',
          borderRadius: '4px',
        },
      }}>
        {holders.slice(0, 10).map((holder, index) => (
          <Box 
            key={holder.address}
            sx={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              mb: 1,
              p: 1,
              bgcolor: 'action.selected',
              borderRadius: 1,
              '&:hover': {
                bgcolor: 'action.hover'
              }
            }}
          >
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Typography color="text.secondary" variant="body2">
                #{index + 1}
              </Typography>
              <Link
                href={`https://solscan.io/account/${holder.address}`}
                target="_blank"
                rel="noopener noreferrer"
                sx={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: 0.5,
                  color: 'primary.main'
                }}
              >
                {`${holder.address.slice(0, 4)}...${holder.address.slice(-4)}`}
                <OpenInNewIcon sx={{ fontSize: '0.9rem' }} />
              </Link>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Typography variant="body2" color="text.secondary">
                {formatAmount(holder.amount)}
              </Typography>
              <Typography variant="body2" sx={{ minWidth: '60px', textAlign: 'right' }}>
                {calculatePercentage(holder.amount).toFixed(2)}%
              </Typography>
            </Box>
          </Box>
        ))}
      </Box>
    </Grid>
  );
};

const TokenInformation = ({ tokenAddress }) => {
  const [tokenInfo, setTokenInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [copySuccess, setCopySuccess] = useState(false);

  useEffect(() => {
    const fetchTokenInfo = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`https://api.jup.ag/tokens/v1/token/${tokenAddress}`);
        setTokenInfo(response.data);
      } catch (err) {
        console.error('Error fetching token info:', err);
        setError('Failed to fetch token information');
      } finally {
        setLoading(false);
      }
    };

    if (tokenAddress) {
      fetchTokenInfo();
    }
  }, [tokenAddress]);

  const handleCopyAddress = async () => {
    try {
      await navigator.clipboard.writeText(tokenAddress);
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  const getTwitterSearchUrl = (address) => {
    return `https://x.com/search?q=${address}&src=typed_query`;
  };

  const formatAddress = (address) => {
    return `${address.slice(0, 4)}...${address.slice(-4)}`;
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString(undefined, { 
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (loading) return <CircularProgress size={24} />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!tokenInfo) return null;

  const SecurityIndicator = ({ isSecure, label }) => (
    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
      <Typography color="text.secondary">{label}</Typography>
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
        {isSecure ? (
          <>
            <CheckCircleIcon color="success" sx={{ fontSize: '1.2rem' }} />
            <Typography color="success.main">Secure</Typography>
          </>
        ) : (
          <>
            <CancelIcon color="error" sx={{ fontSize: '1.2rem' }} />
            <Typography color="error.main">Risk</Typography>
          </>
        )}
      </Box>
    </Box>
  );

  return (
    <Grid container spacing={2}>
      {/* Token Header with Address */}
      <Grid item xs={12}>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'space-between', 
          mb: 2 
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            {tokenInfo.logoURI && (
              <img 
                src={tokenInfo.logoURI} 
                alt={tokenInfo.symbol} 
                style={{ width: 32, height: 32, borderRadius: '50%' }}
              />
            )}
            <Box>
              <Typography variant="h6">{tokenInfo.symbol}</Typography>
              <Typography variant="body2" color="text.secondary">{tokenInfo.name}</Typography>
            </Box>
          </Box>

          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 1,
            bgcolor: 'action.selected',
            p: 0.5,
            borderRadius: 1,
            height: 'fit-content'
          }}>
            <Typography 
              variant="body2" 
              sx={{ 
                fontFamily: 'monospace',
                color: 'text.secondary'
              }}
            >
              {formatAddress(tokenAddress)}
            </Typography>
            <Tooltip title="Copy Address">
              <IconButton 
                size="small" 
                onClick={handleCopyAddress}
                sx={{ color: 'text.secondary' }}
              >
                <ContentCopyIcon fontSize="small" />
              </IconButton>
            </Tooltip>
            <Tooltip title="Search on Twitter">
              <IconButton 
                size="small"
                component="a"
                href={getTwitterSearchUrl(tokenAddress)}
                target="_blank"
                rel="noopener noreferrer"
                sx={{ color: 'text.secondary' }}
              >
                <TwitterIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          </Box>
        </Box>
      </Grid>

      {/* Token Details */}
      <Grid item xs={12}>
        <Grid container spacing={2}>
          {/* Token Info */}
          <Grid item xs={12}>
            <Typography variant="subtitle2" gutterBottom sx={{ color: 'primary.main' }}>
              Token Info
            </Typography>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
              <Typography color="text.secondary">Decimals</Typography>
              <Typography>{tokenInfo.decimals}</Typography>
            </Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
              <Typography color="text.secondary">Created</Typography>
              <Typography>{formatDate(tokenInfo.created_at)}</Typography>
            </Box>
            {tokenInfo.minted_at && (
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                <Typography color="text.secondary">Minted</Typography>
                <Typography>{formatDate(tokenInfo.minted_at)}</Typography>
              </Box>
            )}
          </Grid>

          {/* Security Info */}
          <Grid item xs={12}>
            <Typography variant="subtitle2" gutterBottom sx={{ color: 'primary.main' }}>
              Security Info
            </Typography>
            <SecurityIndicator 
              isSecure={!tokenInfo.mint_authority} 
              label="Mint Authority"
            />
            <SecurityIndicator 
              isSecure={!tokenInfo.freeze_authority} 
              label="Freeze Authority"
            />
          </Grid>

          {/* Tags */}
          {tokenInfo.tags && tokenInfo.tags.length > 0 && (
            <Grid item xs={12}>
              <Typography variant="subtitle2" gutterBottom sx={{ color: 'primary.main' }}>
                Tags
              </Typography>
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {tokenInfo.tags.map((tag, index) => (
                  <Typography 
                    key={index} 
                    variant="body2" 
                    sx={{ 
                      bgcolor: 'action.selected',
                      px: 1,
                      py: 0.5,
                      borderRadius: 1
                    }}
                  >
                    {tag}
                  </Typography>
                ))}
              </Box>
            </Grid>
          )}
        </Grid>
      </Grid>

      {/* Add Token Holders section */}
      <TokenHolders tokenAddress={tokenAddress} />

      {/* Copy Success Snackbar */}
      <Snackbar
        open={copySuccess}
        autoHideDuration={2000}
        onClose={() => setCopySuccess(false)}
        message="Address copied to clipboard"
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      />
    </Grid>
  );
};

