import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  CircularProgress,
  Button,
  Link,
  Tooltip,
  IconButton,
  useTheme,
  Grid,
} from '@mui/material';
import {
  Groups as GroupsIcon,
  ErrorOutline as ErrorOutlineIcon,
  Refresh as RefreshIcon,
  OpenInNew as OpenInNewIcon,
  ContentCopy as ContentCopyIcon,
  CheckCircle as CheckCircleIcon,
} from '@mui/icons-material';
import axios from 'axios';

const commonTypographyStyles = {
  sectionTitle: {
    variant: "body1",
    color: "primary.main",
    mb: 1,
    fontWeight: 500
  },
  label: {
    variant: "caption",
    color: "text.secondary"
  },
  value: {
    variant: "caption",
    fontWeight: 500
  }
};

const TokenHolders = ({ tokenAddress }) => {
  const [holders, setHolders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [copySuccess, setCopySuccess] = useState({});
  const theme = useTheme();

  const fetchHolders = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await axios.get(`https://api.solscan.io/token/holders?token=${tokenAddress}`);
      setHolders(response.data.data || []);
    } catch (err) {
      console.error('Error fetching holders:', err);
      setError('Failed to load holder information');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (tokenAddress) {
      fetchHolders();
    }
  }, [tokenAddress]);

  const handleRetry = () => {
    fetchHolders();
  };

  const calculatePercentage = (amount) => {
    const totalSupply = holders.reduce((sum, holder) => sum + (holder.amount || 0), 0);
    return totalSupply ? (amount / totalSupply) * 100 : 0;
  };

  const calculateTop10Percentage = () => {
    const top10 = holders.slice(0, 10);
    const top10Total = top10.reduce((sum, holder) => sum + calculatePercentage(holder.amount), 0);
    return top10Total.toFixed(2);
  };

  const handleCopyAddress = async (address) => {
    try {
      await navigator.clipboard.writeText(address);
      setCopySuccess(prev => ({ ...prev, [address]: true }));
      setTimeout(() => {
        setCopySuccess(prev => ({ ...prev, [address]: false }));
      }, 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
        <CircularProgress size={24} />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ 
        p: 2, 
        textAlign: 'center',
        bgcolor: 'action.selected',
        borderRadius: 1
      }}>
        <Typography color="error" variant="body2" gutterBottom>
          {error}
        </Typography>
        <Button 
          size="small" 
          onClick={handleRetry}
          startIcon={<RefreshIcon />}
        >
          Retry
        </Button>
      </Box>
    );
  }

  if (!holders?.length) {
    return (
      <Typography color="text.secondary" variant="body2" sx={{ p: 2 }}>
        No holder data available
      </Typography>
    );
  }

  return (
    <Grid item xs={12} sx={{ flex: 1, minHeight: 0 }}>
      <Box sx={{ 
        display: 'flex', 
        flexDirection: 'column',
        height: '100%',
      }}>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          mb: 1
        }}>
          <Typography {...commonTypographyStyles.sectionTitle}>
            Top Holders
          </Typography>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 1,
            bgcolor: 'action.selected',
            px: 1.5,
            py: 0.5,
            borderRadius: 1
          }}>
            <Typography {...commonTypographyStyles.label}>
              Combined:
            </Typography>
            <Typography {...commonTypographyStyles.value}>
              {calculateTop10Percentage()}%
            </Typography>
          </Box>
        </Box>

        <Box sx={{ 
          flex: 1,
          minHeight: 0,
          overflowY: 'auto',
          '&::-webkit-scrollbar': {
            width: '6px',
          },
          '&::-webkit-scrollbar-track': {
            background: 'transparent',
          },
          '&::-webkit-scrollbar-thumb': {
            background: 'rgba(0,0,0,0.1)',
            borderRadius: '3px',
          }
        }}>
          {holders.slice(0, 10).map((holder, index) => (
            <Box 
              key={holder.address}
              sx={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                mb: 0.75,
                py: 0.75,
                px: 1.5,
                bgcolor: 'action.selected',
                borderRadius: 1,
                '&:hover': {
                  bgcolor: 'action.hover'
                },
                '&:last-child': {
                  mb: 0
                }
              }}
            >
              <Box sx={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: 1
              }}>
                <Typography {...commonTypographyStyles.label}>
                  #{index + 1}
                </Typography>
                <Link
                  href={`https://solscan.io/account/${holder.address}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  sx={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: 0.5,
                    color: 'primary.main',
                    textDecoration: 'none',
                    '&:hover': {
                      textDecoration: 'underline'
                    }
                  }}
                >
                  <Typography {...commonTypographyStyles.value}>
                    {`${holder.address.slice(0, 4)}...${holder.address.slice(-4)}`}
                  </Typography>
                  <OpenInNewIcon sx={{ fontSize: '1.2rem' }} />
                </Link>
              </Box>
              <Typography {...commonTypographyStyles.value}>
                {calculatePercentage(holder.amount).toFixed(2)}%
              </Typography>
            </Box>
          ))}
        </Box>
      </Box>
    </Grid>
  );
};

export default TokenHolders;
