import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Paper,
  useTheme,
  Link,
  CircularProgress,
  IconButton,
  Tooltip,
  Snackbar
} from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import TwitterIcon from '@mui/icons-material/Twitter';
import { getPairXToken } from '../../utils/helpers';
import axios from 'axios';

const TimeframeStats = ({ label, txns, volume, priceChange }) => {
  // Add null checks with default values
  const safeVolume = volume ?? 0;
  const safePriceChange = priceChange ?? 0;
  const safeTxns = txns ?? { buys: 0, sells: 0 };

  return (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle2" color="primary.main" gutterBottom>
        {label}
      </Typography>
      <Grid container spacing={2}>
        {/* Transactions */}
        <Grid item xs={12}>
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            mb: 1,
            bgcolor: 'action.selected',
            p: 1,
            borderRadius: 1
          }}>
            <Box>
              <Typography color="text.secondary" variant="body2">Buys</Typography>
              <Typography 
                variant="body1" 
                color="success.main"
                sx={{ fontWeight: 500 }}
              >
                {safeTxns.buys}
              </Typography>
            </Box>
            <Box sx={{ textAlign: 'right' }}>
              <Typography color="text.secondary" variant="body2">Sells</Typography>
              <Typography 
                variant="body1" 
                color="error.main"
                sx={{ fontWeight: 500 }}
              >
                {safeTxns.sells}
              </Typography>
            </Box>
          </Box>
        </Grid>

        {/* Volume and Price Change */}
        <Grid item xs={12}>
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between',
            bgcolor: 'action.selected',
            p: 1,
            borderRadius: 1
          }}>
            <Box>
              <Typography color="text.secondary" variant="body2">Volume</Typography>
              <Typography variant="body1" sx={{ fontWeight: 500 }}>
                ${Number(safeVolume).toLocaleString()}
              </Typography>
            </Box>
            <Box sx={{ textAlign: 'right' }}>
              <Typography color="text.secondary" variant="body2">Price Change</Typography>
              <Typography 
                variant="body1" 
                color={safePriceChange >= 0 ? 'success.main' : 'error.main'}
                sx={{ fontWeight: 500 }}
              >
                {safePriceChange >= 0 ? '+' : ''}{safePriceChange.toFixed(2)}%
              </Typography>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
};

const StatRow = ({ label, txns, volume, priceChange }) => {
  if (!txns || !volume) return null;

  const total = txns.buys + txns.sells;
  const buyPercent = (txns.buys / total) * 100;
  const sellPercent = (txns.sells / total) * 100;

  return (
    <Box sx={{ mb: 3 }}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between',
        alignItems: 'center',
        mb: 1
      }}>
        <Typography variant="subtitle2" color="primary.main">
          {label}
        </Typography>
        <Box sx={{ 
          display: 'flex',
          alignItems: 'center',
          bgcolor: priceChange >= 0 ? 'success.main' : 'error.main',
          px: 1,
          py: 0.5,
          borderRadius: 1,
        }}>
          <Typography 
            variant="body2" 
            sx={{ 
              color: 'white',
              fontWeight: 500 
            }}
          >
            {priceChange >= 0 ? '+' : ''}{priceChange?.toFixed(2)}%
          </Typography>
        </Box>
      </Box>

      {/* Volume Row */}
      <Box sx={{ mb: 2 }}>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          alignItems: 'center',
          mb: 0.5 
        }}>
          <Typography variant="body2" color="text.secondary">
            Volume
          </Typography>
          <Typography variant="body2">
            ${Number(volume).toLocaleString()}
          </Typography>
        </Box>

        <Box sx={{ 
          display: 'flex', 
          height: '24px',
          borderRadius: 1,
          overflow: 'hidden',
          bgcolor: 'background.paper',
          border: 1,
          borderColor: 'divider'
        }}>
          <Box sx={{ 
            width: `${54.95}%`,
            bgcolor: 'success.main',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <Typography 
              variant="caption" 
              sx={{ color: 'white', fontWeight: 500 }}
            >
              ${Number(volume * 0.5495).toLocaleString()}
            </Typography>
          </Box>
          <Box sx={{ 
            width: `${45.05}%`,
            bgcolor: 'error.main',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <Typography 
              variant="caption" 
              sx={{ color: 'white', fontWeight: 500 }}
            >
              ${Number(volume * 0.4505).toLocaleString()}
            </Typography>
          </Box>
        </Box>

        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          mt: 0.5
        }}>
          <Typography variant="caption" color="success.main">
            54.95%
          </Typography>
          <Typography variant="caption" color="error.main">
            45.05%
          </Typography>
        </Box>
      </Box>

      {/* Transactions Row */}
      <Box>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          alignItems: 'center',
          mb: 0.5 
        }}>
          <Typography variant="body2" color="text.secondary">
            Transactions
          </Typography>
          <Typography variant="body2">
            {total}
          </Typography>
        </Box>

        <Box sx={{ 
          display: 'flex', 
          height: '24px',
          borderRadius: 1,
          overflow: 'hidden',
          bgcolor: 'background.paper',
          border: 1,
          borderColor: 'divider'
        }}>
          <Box sx={{ 
            width: `${buyPercent}%`,
            bgcolor: 'success.main',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <Typography 
              variant="caption" 
              sx={{ color: 'white', fontWeight: 500 }}
            >
              {txns.buys}
            </Typography>
          </Box>
          <Box sx={{ 
            width: `${sellPercent}%`,
            bgcolor: 'error.main',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <Typography 
              variant="caption" 
              sx={{ color: 'white', fontWeight: 500 }}
            >
              {txns.sells}
            </Typography>
          </Box>
        </Box>

        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          mt: 0.5
        }}>
          <Typography variant="caption" color="success.main">
            {buyPercent.toFixed(2)}%
          </Typography>
          <Typography variant="caption" color="error.main">
            {sellPercent.toFixed(2)}%
          </Typography>
        </Box>
      </Box>
    </Box>
  );
};

const MarketStats = ({ pairAddress }) => {
  const [marketData, setMarketData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMarketData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`https://api.dexscreener.com/latest/dex/pairs/solana/${pairAddress}`);
        const pairData = response.data.pairs?.[0];
        if (!pairData) {
          throw new Error('No pair data found');
        }
        setMarketData(pairData);
      } catch (err) {
        console.error('Error fetching market data:', err);
        setError('Failed to fetch market data');
      } finally {
        setLoading(false);
      }
    };

    if (pairAddress) {
      fetchMarketData();
    }
  }, [pairAddress]);

  if (loading) return <CircularProgress size={24} />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!marketData) return null;

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Market Activity
      </Typography>

      <Grid container spacing={2}>
        <Grid item xs={12} md={6}>
          <StatRow 
            label="Last 5 Minutes"
            txns={marketData.txns?.m5}
            volume={marketData.volume?.m5}
            priceChange={marketData.priceChange?.m5}
          />

          <StatRow 
            label="Last Hour"
            txns={marketData.txns?.h1}
            volume={marketData.volume?.h1}
            priceChange={marketData.priceChange?.h1}
          />
        </Grid>

        <Grid item xs={12} md={6}>
          <StatRow 
            label="Last 6 Hours"
            txns={marketData.txns?.h6}
            volume={marketData.volume?.h6}
            priceChange={marketData.priceChange?.h6}
          />

          <StatRow 
            label="Last 24 Hours"
            txns={marketData.txns?.h24}
            volume={marketData.volume?.h24}
            priceChange={marketData.priceChange?.h24}
          />
        </Grid>
      </Grid>
    </Box>
  );
};

const TokenHolders = ({ tokenAddress }) => {
  const [holders, setHolders] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHolders = async () => {
      try {
        setLoading(true);
        const response = await axios.post(
          'https://mainnet.helius-rpc.com/?api-key=498ead61-0162-4f2b-b6ec-617ae17935d0',
          {
            jsonrpc: '2.0',
            id: 1,
            method: 'getTokenLargestAccounts',
            params: [tokenAddress]
          }
        );

        if (response.data.result) {
          setHolders(response.data.result.value);
        }
      } catch (err) {
        console.error('Error fetching token holders:', err);
        setError('Failed to fetch token holders');
      } finally {
        setLoading(false);
      }
    };

    if (tokenAddress) {
      fetchHolders();
    }
  }, [tokenAddress]);

  const calculatePercentage = (amount) => {
    if (!holders) return 0;
    const totalSupply = holders.reduce((sum, holder) => sum + Number(holder.amount), 0);
    return (Number(amount) / totalSupply) * 100;
  };

  const calculateTop10Percentage = () => {
    if (!holders) return 0;
    const top10Total = holders.slice(0, 10).reduce((sum, holder) => sum + Number(holder.amount), 0);
    const totalSupply = holders.reduce((sum, holder) => sum + Number(holder.amount), 0);
    return ((top10Total / totalSupply) * 100).toFixed(2);
  };

  const formatAmount = (amount) => {
    return Number(amount).toLocaleString();
  };

  if (loading) return <CircularProgress size={24} />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!holders) return null;

  return (
    <Grid item xs={12}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 1
      }}>
        <Typography variant="subtitle2" color="primary.main">
          Top Holders
        </Typography>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: 1,
          bgcolor: 'action.selected',
          px: 1,
          py: 0.5,
          borderRadius: 1
        }}>
          <Typography variant="caption" color="text.secondary">
            Combined:
          </Typography>
          <Typography 
            variant="caption" 
            sx={{ fontWeight: 500 }}
          >
            {calculateTop10Percentage()}%
          </Typography>
        </Box>
      </Box>

      <Box sx={{ 
        maxHeight: '180px', 
        overflowY: 'auto',
        '&::-webkit-scrollbar': {
          width: '6px',
        },
        '&::-webkit-scrollbar-track': {
          background: 'transparent',
        },
        '&::-webkit-scrollbar-thumb': {
          background: 'rgba(0,0,0,0.1)',
          borderRadius: '3px',
        },
      }}>
        {holders.slice(0, 10).map((holder, index) => (
          <Box 
            key={holder.address}
            sx={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              mb: 0.75,
              py: 0.5,
              px: 1,
              bgcolor: 'action.selected',
              borderRadius: 1,
              '&:hover': {
                bgcolor: 'action.hover'
              }
            }}
          >
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: 1,
              minWidth: 0 // Allows text truncation
            }}>
              <Typography 
                color="text.secondary" 
                variant="caption"
                sx={{ flexShrink: 0 }}
              >
                #{index + 1}
              </Typography>
              <Link
                href={`https://solscan.io/account/${holder.address}`}
                target="_blank"
                rel="noopener noreferrer"
                sx={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: 0.5,
                  color: 'primary.main',
                  textDecoration: 'none',
                  '&:hover': {
                    textDecoration: 'underline'
                  }
                }}
              >
                <Typography 
                  variant="caption" 
                  sx={{ 
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    whiteSpace: 'nowrap'
