const RugCheckReport = ({ tokenAddress }) => {
  const [reportData, setReportData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRugCheckReport = async () => {
      try {
        setLoading(true);
        const response = await axios.get(
          `https://api.rugcheck.xyz/v1/tokens/${tokenAddress}/report/summary`
        );
        setReportData(response.data);
      } catch (err) {
        console.error('Error fetching RugCheck report:', err);
        setError('Failed to fetch security report');
      } finally {
        setLoading(false);
      }
    };

    if (tokenAddress) {
      fetchRugCheckReport();
    }
  }, [tokenAddress]);

  const getRiskColor = (level) => {
    switch (level) {
      case 'danger':
        return 'error.main';
      case 'warn':
        return 'warning.main';
      default:
        return 'success.main';
    }
  };

  if (loading) return <CircularProgress size={24} />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!reportData) return null;

  return (
    <Paper sx={{ p: 2, height: '100%' }}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 2 
      }}>
        <Typography variant="h6">Security Report</Typography>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center',
          gap: 1,
          bgcolor: 'action.selected',
          px: 1.5,
          py: 0.5,
          borderRadius: 1
        }}>
          <Typography variant="body2" color="text.secondary">
            Score:
          </Typography>
          <Typography 
            variant="body2" 
            sx={{ 
              fontWeight: 500,
              color: reportData.score > 50000 ? 'success.main' : 'error.main'
            }}
          >
            {reportData.score.toLocaleString()}
          </Typography>
        </Box>
      </Box>

      <Box sx={{ 
        display: 'flex',
        flexDirection: 'column',
        gap: 1.5
      }}>
        {reportData.risks.map((risk, index) => (
          <Box 
            key={index}
            sx={{ 
              p: 1.5,
              borderRadius: 1,
              bgcolor: 'action.selected',
              border: 1,
              borderColor: getRiskColor(risk.level),
            }}
          >
            <Box sx={{ 
              display: 'flex', 
              justifyContent: 'space-between',
              alignItems: 'center',
              mb: 0.5
            }}>
              <Typography 
                variant="body2" 
                sx={{ 
                  fontWeight: 500,
                  color: getRiskColor(risk.level)
                }}
              >
                {risk.name}
              </Typography>
              {risk.value && (
                <Typography 
                  variant="body2" 
                  sx={{ 
                    fontWeight: 500,
                    color: getRiskColor(risk.level)
                  }}
                >
                  {risk.value}
                </Typography>
              )}
            </Box>
            <Typography variant="caption" color="text.secondary">
              {risk.description}
            </Typography>
          </Box>
        ))}
      </Box>
    </Paper>
  );
};

