import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Grid,
  Paper,
  CircularProgress,
  Button,
  Divider,
  useTheme
} from '@mui/material';
import {
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  ErrorOutline as ErrorOutlineIcon,
  Refresh as RefreshIcon,
  ShowChart as ShowChartIcon
} from '@mui/icons-material';
import axios from 'axios';

const TimeframeStats = ({ label, data }) => {
  const theme = useTheme();

  if (!data) return null;

  const { volume, priceChange, txns } = data;
  const totalTxns = (txns?.buys || 0) + (txns?.sells || 0);
  const buyPercent = totalTxns ? ((txns?.buys || 0) / totalTxns) * 100 : 0;
  const sellPercent = totalTxns ? ((txns?.sells || 0) / totalTxns) * 100 : 0;

  return (
    <Paper
      elevation={0}
      sx={{
        p: 2,
        mb: 2,
        border: 1,
        borderColor: 'divider',
        borderRadius: 2,
        bgcolor: 'background.default'
      }}
    >
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between',
        mb: 2 
      }}>
        <Typography variant="subtitle2" sx={{ fontWeight: 600 }}>
          {label}
        </Typography>
        <Box
          sx={{
            display: 'flex',
            alignItems: 'center',
            gap: 0.5,
            px: 1,
            py: 0.5,
            borderRadius: 1,
            bgcolor: (priceChange || 0) >= 0 ? 'success.lighter' : 'error.lighter',
            color: (priceChange || 0) >= 0 ? 'success.main' : 'error.main'
          }}
        >
          {(priceChange || 0) >= 0 ? <TrendingUpIcon fontSize="small" /> : <TrendingDownIcon fontSize="small" />}
          <Typography variant="caption" sx={{ fontWeight: 600 }}>
            {(priceChange || 0) >= 0 ? '+' : ''}{(priceChange || 0)?.toFixed(2)}%
          </Typography>
        </Box>
      </Box>

      <Grid container spacing={2}>
        {/* Volume Stats */}
        <Grid item xs={12}>
          <Box sx={{ mb: 2 }}>
            <Box sx={{ 
              display: 'flex', 
              justifyContent: 'space-between',
              mb: 1
            }}>
              <Typography variant="caption" color="text.secondary">
                Volume
              </Typography>
              <Typography variant="caption" sx={{ fontWeight: 500 }}>
                ${(volume || 0).toLocaleString()}
              </Typography>
            </Box>
            <Box sx={{ 
              height: 8,
              borderRadius: 4,
              bgcolor: 'background.paper',
              overflow: 'hidden',
              display: 'flex'
            }}>
              <Box 
                sx={{ 
                  width: `${buyPercent}%`,
                  bgcolor: 'success.main',
                  transition: 'width 0.3s ease'
                }} 
              />
              <Box 
                sx={{ 
                  width: `${sellPercent}%`,
                  bgcolor: 'error.main',
                  transition: 'width 0.3s ease'
                }} 
              />
            </Box>
            <Box sx={{ 
              display: 'flex', 
              justifyContent: 'space-between',
              mt: 0.5
            }}>
              <Typography variant="caption" color="success.main">
                Buy {buyPercent.toFixed(1)}%
              </Typography>
              <Typography variant="caption" color="error.main">
                Sell {sellPercent.toFixed(1)}%
              </Typography>
            </Box>
          </Box>
        </Grid>

        {/* Transaction Stats */}
        <Grid item xs={6}>
          <Box sx={{ 
            p: 1.5,
            borderRadius: 2,
            bgcolor: 'success.lighter',
            textAlign: 'center'
          }}>
            <Typography variant="caption" color="success.main" sx={{ display: 'block', mb: 0.5 }}>
              Buy Txns
            </Typography>
            <Typography variant="subtitle2" color="success.main" sx={{ fontWeight: 600 }}>
              {txns?.buys || 0}
            </Typography>
          </Box>
        </Grid>
        <Grid item xs={6}>
          <Box sx={{ 
            p: 1.5,
            borderRadius: 2,
            bgcolor: 'error.lighter',
            textAlign: 'center'
          }}>
            <Typography variant="caption" color="error.main" sx={{ display: 'block', mb: 0.5 }}>
              Sell Txns
            </Typography>
            <Typography variant="subtitle2" color="error.main" sx={{ fontWeight: 600 }}>
              {txns?.sells || 0}
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Paper>
  );
};

const MarketStats = ({ pair }) => {
  const [marketData, setMarketData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMarketData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Replace with your actual API endpoint
        const response = await axios.get(`https://your-api/market-stats/${pair?.address}`);

        // Mock data structure - replace with actual API response handling
        setMarketData({
          '30min': {
            volume: 1000000,
            priceChange: 2.5,
            txns: { buys: 150, sells: 100 }
          },
          '4h': {
            volume: 5000000,
            priceChange: -1.2,
            txns: { buys: 450, sells: 380 }
          },
          '24h': {
            volume: 15000000,
            priceChange: 5.8,
            txns: { buys: 1200, sells: 900 }
          }
        });
      } catch (err) {
        console.error('Error fetching market data:', err);
        setError('Failed to load market statistics');
      } finally {
        setLoading(false);
      }
    };

    if (pair?.address) {
      fetchMarketData();
    }
  }, [pair?.address]);

  if (loading) {
    return (
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center',
        height: 200 
      }}>
        <CircularProgress size={24} />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ 
        p: 3, 
        textAlign: 'center',
        bgcolor: 'error.lighter',
        borderRadius: 2
      }}>
        <ErrorOutlineIcon color="error" sx={{ fontSize: 40, mb: 1 }} />
        <Typography color="error.main" gutterBottom>
          {error}
        </Typography>
        <Button 
          startIcon={<RefreshIcon />}
          onClick={() => setMarketData(null)}
          size="small"
          sx={{ mt: 1 }}
        >
          Retry
        </Button>
      </Box>
    );
  }

  return (
    <Box>
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        gap: 1,
        mb: 3 
      }}>
        <ShowChartIcon color="primary" />
        <Typography variant="h6" sx={{ fontWeight: 600 }}>
          Market Activity
        </Typography>
      </Box>

      <TimeframeStats label="Last 30 Minutes" data={marketData?.['30min']} />
      <TimeframeStats label="Last 4 Hours" data={marketData?.['4h']} />
      <TimeframeStats label="Last 24 Hours" data={marketData?.['24h']} />
    </Box>
  );
};

export default MarketStats;
