import { Box, Link, Typography } from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';
import { getMeteoraLink } from '../../utils/helpers';

// Helper function to format numbers
const formatNumber = (value) => {
  const num = Number(value);
  return `$${num.toLocaleString(undefined, { maximumFractionDigits: 0 })}`;
};

// Helper function to format percentages
const formatPercentage = (value) => {
  const num = Number(value);
  return `${num.toLocaleString(undefined, { maximumFractionDigits: 0 })}%`;
};

export const getColumns = () => [
  { 
    id: 'pairName', 
    label: 'Pair Name', 
    numeric: false,
    render: (row) => (
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        <Typography 
          sx={{ 
            color: 'text.primary',
            fontWeight: 500,
            display: 'flex',
            alignItems: 'center',
            gap: 0.5
          }}
        >
          {row.pairName}
          <Typography 
            component="span" 
            sx={{ 
              color: 'text.secondary',
              fontSize: 'inherit'
            }}
          >
            â—Ž
          </Typography>
        </Typography>
        <Box sx={{ 
          display: 'flex',
          alignItems: 'center',
          gap: 0.5,
          bgcolor: 'rgba(255, 255, 255, 0.05)', // Darker background
          px: 0.75,
          py: 0.25,
          borderRadius: '4px',
          height: 'fit-content'
        }}>
          <Typography 
            variant="caption" 
            sx={{ 
              color: row.priceChange >= 0 ? '#00ff00' : '#ff4444', // More vibrant colors
              fontWeight: 500,
              fontSize: '0.75rem'
            }}
          >
            {row.priceChange >= 0 ? '+' : ''}{row.priceChange}%
          </Typography>
          <Box sx={{
            display: 'flex',
            alignItems: 'center',
            gap: 0.5,
            pl: 0.5,
            ml: 0.5,
            borderLeft: 1,
            borderColor: 'rgba(255, 255, 255, 0.1)' // Subtle divider
          }}>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center',
              gap: 0.5 
            }}>
              <Typography 
                variant="caption" 
                sx={{ 
                  color: '#9945ff', // Purple color for BIN
                  fontWeight: 500,
                  fontSize: '0.75rem'
                }}
              >
                BIN
              </Typography>
              <Typography 
                variant="caption" 
                sx={{ 
                  color: 'text.primary',
                  fontWeight: 500,
                  fontSize: '0.75rem'
                }}
              >
                {row.binStep}
              </Typography>
            </Box>
          </Box>
        </Box>
      </Box>
    )
  },
  { id: 'binStep', label: 'Bin Step', numeric: true },
  { id: 'baseFee', label: 'Base Fee %', numeric: true },
  {
    id: 'volume30min',
    label: '30min Volume',
    numeric: true,
    render: (row) => formatNumber(row.volume30min)
  },
  {
    id: 'fees30min',
    label: '30min Fees',
    numeric: true,
    render: (row) => formatNumber(row.fees30min)
  },
  {
    id: 'fees24h',
    label: '24h Fees',
    numeric: true,
    render: (row) => formatNumber(row.fees24h)
  },
  {
    id: 'apr',
    label: '24h Fee/TVL (APR)',
    numeric: true,
    render: (row) => formatPercentage(row.apr)
  },
  {
    id: 'totalLiquidity',
    label: 'Total Liquidity',
    numeric: true,
    render: (row) => formatNumber(row.totalLiquidity)
  },
  {
    id: 'totalVolume',
    label: 'Total Volume',
    numeric: true,
    render: (row) => formatNumber(row.totalVolume)
  }
];
